package FruitInheritance;

public class Fruit {
	private String name;

	public Fruit(String name) {
		this.name = name;
	}

	public Object taste() {
		// TODO Auto-generated method stub
		return name;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
	
	
	

}